INSERT INTO `scenic_spot` VALUES ('447', '归义县旧址', ' 广西·桂林  ', '桂林', 'NA', '唐朝武德四年（公元621年），在遇龙河畔的仙桂村边设置归义县，贞观元年（公元627年）废止。遗址旁仙桂村（现为旧县村）为阳朔名村之一，历代达官显贵层出不穷，现村中仍保存有部分古建筑，城墙残垣、归义古井等值得一看。', null, 'NA', 'NA', '79', '334', '334', '3', null);
