DROP PROCEDURE IF EXISTS `fun_Random2to5Stars_hotels`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `fun_Random2to5Stars_hotels`()
    COMMENT '设置酒店的星级随机2-5星'
BEGIN
	UPDATE hotels SET stars = FLOOR(2+(RAND()*4))
		WHERE stars IS NULL;

	-- UPDATE hotels SET stars = NULL
	-- 	WHERE stars IS NOT NULL;
END
;;
DELIMITER ;

